def patternMatching(pattern, genome):
    startPattIndcs = ''
    for i in range(len(genome)-len(pattern)):
        newPattern = genome[i:i+len(pattern)]
        if pattern == newPattern:
            if len(startPattIndcs) == 0:
                startPattIndcs += str(i)
            else:
                startPattIndcs +=  ' ' + str(i)
    return startPattIndcs

